import MachinesIcon from "../../images/dumbbell.png";
import PersonalTrainerIcon from "../../images/personal.png";
import MembershipIcon from "../../images/membership.png";

import { CountUp } from 'https://cdnjs.cloudflare.com/ajax/libs/countup.js/2.0.7/countUp.js'


const GymInfos = () => {


    function countStart() {
        const $counters = document.querySelectorAll(".info-number"),
            options = {
                useEasing: true,
                useGrouping: true,
                separator: ",",
                decimal: "."
            };

        $counters.forEach((item) => {
            const value = item.dataset.value;
            const counter = new CountUp(item, value, options);
            counter.start();
        });
    }

    new Waypoint({
        element: document.querySelector('.level'),
        handler: function () {
            countStart()
            //this.destroy() //for once
        },
        offset: '50%'
    });


    return (
        <div className="three-gym-info">
            <div className="gym-info" id="info-machines">
                <div className="info-holders">
                    <img className="info-icon" src={MachinesIcon} />
                </div>
                <div className="info-holders">
                    <span className="info-number"> 35+ </span><br />
                    <span className="info-desc">MACHINES</span>
                </div>

            </div>
            <div className="gym-info" id="info-trainers">
                <div className="info-holders">
                    <img className="info-icon" src={PersonalTrainerIcon} />
                </div>
                <div className="info-holders">
                    <span className="info-number"> 8 </span><br />
                    <span className="info-desc">TRAINERS</span>
                </div>

            </div>
            <div className="gym-info" id="info-memberships">
                <div className="info-holders">
                    <img className="info-icon" src={MembershipIcon} />
                </div>
                <div className="info-holders">
                    <span className="info-number"> 100+ </span><br />
                    <span className="info-desc">MEMBERS</span>
                </div>
            </div>
        </div>
    )

}

export default GymInfos;
